// ===== COMPETITION ENTRE GEMINI ET OPENAI =====
const allInputs = $input.all();
const requestId = $('Init Tracking').first().json.requestId;
const startTime = $('Init Tracking').first().json.startTime;

let geminiResult = null;
let openaiResult = null;

// Récupérer les 2 résultats
if (allInputs.length >= 2) {
  openaiResult = allInputs[0].json;
  geminiResult = allInputs[1].json; 
} else if (allInputs.length === 1) {
  geminiResult = allInputs[0].json;
  openaiResult = { error: 'OpenAI failed', output: null };
}

// ===== FONCTION DE SCORING SIMPLIFIÉE =====
function scoreResult(result, modelName) {
  let score = 0;

  // Vérifier si le résultat existe et est valide
  if (!result || !result.output || result.error) {
    return { model: modelName, totalScore: 0, result: result };
  }

  // Compter les champs critiques remplis
  const critical = [
    result.output.employeur?.nom_raison_sociale,
    result.output.employeur?.siret,
    result.output.victime?.nom,
    result.output.victime?.prenom,
    result.output.accident?.date,
    result.output.type_declaration
  ];

  const filled = critical.filter(field => field && String(field).trim() !== '' && String(field).trim() !== 'null').length;
  score = (filled / critical.length) * 100;

  // Bonus pour structure complète
  if (result.output.employeur && Object.keys(result.output.employeur).length > 3) score += 10;
  if (result.output.victime && Object.keys(result.output.victime).length > 4) score += 10;

  return {
    model: modelName,
    totalScore: Math.round(score),
    result: result
  };
}

// ===== ÉVALUATION =====
const geminiScore = scoreResult(geminiResult, 'Gemini');
const openaiScore = scoreResult(openaiResult, 'OpenAI');

// ===== SÉLECTION DU GAGNANT =====
let winner;
if (geminiScore.totalScore > openaiScore.totalScore) {
  winner = geminiScore;
} else {
  winner = openaiScore;
}

// ===== HYBRIDATION SI SCORES PROCHES =====
if (Math.abs(geminiScore.totalScore - openaiScore.totalScore) < 15 && 
    geminiResult?.output && openaiResult?.output) {

  // Combiner les meilleurs champs
  const hybrid = { ...winner.result.output };

  const loser = (winner.model === 'Gemini') ? openaiResult : geminiResult;

  // Récupérer champs manquants du perdant
  if ((!hybrid.employeur?.siret || hybrid.employeur?.siret === 'null') && loser.output?.employeur?.siret && loser.output.employeur.siret !== 'null') {
    hybrid.employeur.siret = loser.output.employeur.siret;
  }
  if ((!hybrid.victime?.numero_secu || hybrid.victime?.numero_secu === 'null') && loser.output?.victime?.numero_secu && loser.output.victime.numero_secu !== 'null') {
    hybrid.victime.numero_secu = loser.output.victime.numero_secu;
  }

  winner.result.output = hybrid;
}

// ===== VÉRIFICATION FINALE =====
// IMPORTANT: Utiliser !! pour convertir en boolean
const hasOutput = !!winner.result?.output;
const hasValidScore = winner.totalScore > 20;
const success = hasValidScore && hasOutput;

console.log('=== AI COMPETITION ===');
console.log('Gemini:', geminiScore.totalScore);
console.log('OpenAI:', openaiScore.totalScore);
console.log('Winner:', winner.model);
console.log('Success:', success);

// ===== RETOUR COMPATIBLE =====
return {
  output: winner.result?.output || {},
  success: success,  // Maintenant c'est un boolean true/false
  fallback_mode: !success,
  error_message: success ? '' : 'Both AI failed',
  elapsed_time: Date.now() - startTime,
  requestId: requestId,
  winner_model: winner.model
};

// CONSTRUCTION DE LA REPONSE FINALE FRONT-READY
const input = $input.first().json;
const responseForWebsite = input.responseForWebsite;
const requestId = input.requestId;

// URL de votre site - MISE À JOUR VERCEL
const SITE_URL = 'https://accidentdoc-web.vercel.app';

// Construction de la réponse finale front-ready
const finalResponse = {
  ok: responseForWebsite.success !== false,
  requestId: requestId,
  next: responseForWebsite.success !== false ? `${SITE_URL}/validation?rid=${requestId}` : `${SITE_URL}/upload?error=invalid_document`,
  payload: responseForWebsite
};

console.log('=== FINAL RESPONSE ===');
console.log('Request ID:', requestId);
console.log('Success:', finalResponse.ok);
console.log('Fallback mode:', responseForWebsite.fallback_mode || false);
console.log('Document Type:', responseForWebsite.documentType);
console.log('Session ID:', responseForWebsite.sessionId);
if (responseForWebsite.completionStats) {
  console.log('Completion Rate:', responseForWebsite.completionStats.completionRate + '%');
}
if (responseForWebsite.errorDetails) {
  console.log('ERROR:', responseForWebsite.errorDetails.message);
}

return finalResponse;

// PREPARATION DES DONNEES POUR VALIDATION UTILISATEUR (MODE NORMAL)
const finalResult = $input.first().json.resultat_final;
const extractedData = finalResult.declaration;
const sessionId = 'validation_' + Date.now() + '_' + Math.random().toString(36).substr(2, 6);
const requestId = $('Init Tracking').first().json.requestId;

// Analyser le type de document pour adapter les champs
const docType = extractedData.type;
const isInterim = docType === 'AT_INTERIM';
const isMaladiePro = docType === 'MALADIE_PROFESSIONNELLE';

// STRUCTURE DES CHAMPS A VALIDER selon le type
let fieldsToValidate = [];

// Champs communs
const commonFields = [
  { section: 'employeur', field: 'nom_raison_sociale', label: 'Raison sociale employeur', required: true },
  { section: 'employeur', field: 'siret', label: 'SIRET employeur', required: true },
  { section: 'employeur', field: 'adresse', label: 'Adresse employeur', required: true },
  { section: 'victime', field: 'nom', label: 'Nom de la victime', required: true },
  { section: 'victime', field: 'prenom', label: 'Prénom de la victime', required: true },
  { section: 'victime', field: 'numero_secu', label: 'Numéro sécurité sociale', required: true },
  { section: 'victime', field: 'date_naissance', label: 'Date de naissance', required: true },
  { section: 'victime', field: 'adresse', label: 'Adresse de la victime', required: true },
  { section: 'victime', field: 'profession', label: 'Profession', required: true }
];

// Champs spécifiques selon le type
if (isMaladiePro) {
  fieldsToValidate = commonFields.concat([
    { section: 'maladie', field: 'date', label: 'Date des premiers symptômes', required: true },
    { section: 'maladie', field: 'lieu', label: 'Lieu d\'exposition', required: false },
    { section: 'maladie', field: 'activite_victime', label: 'Activité exposante', required: false }
  ]);
} else {
  fieldsToValidate = commonFields.concat([
    { section: 'accident', field: 'date', label: 'Date de l\'accident', required: true },
    { section: 'accident', field: 'heure', label: 'Heure de l\'accident', required: true },
    { section: 'accident', field: 'lieu', label: 'Lieu de l\'accident', required: true },
    { section: 'accident', field: 'activite_victime', label: 'Activité au moment de l\'accident', required: true },
    { section: 'accident', field: 'nature_accident', label: 'Nature de l\'accident', required: true },
    { section: 'accident', field: 'siege_lesions', label: 'Siège des lésions', required: false },
    { section: 'accident', field: 'nature_lesions', label: 'Nature des lésions', required: false }
  ]);

  if (isInterim) {
    fieldsToValidate = fieldsToValidate.concat([
      { section: 'interim', field: 'entreprise_travail_temporaire', label: 'Entreprise de travail temporaire', required: true },
      { section: 'interim', field: 'siret_agence', label: 'SIRET de l\'agence', required: true }
    ]);
  }
}

// EXTRACTION DES VALEURS ACTUELLES
const currentValues = {};
for (let i = 0; i < fieldsToValidate.length; i++) {
  const field = fieldsToValidate[i];
  const sectionData = extractedData[field.section] || {};
  const value = sectionData[field.field] || null;

  const fieldKey = field.section + '.' + field.field;
  currentValues[fieldKey] = {
    label: field.label,
    value: value,
    section: field.section,
    field: field.field,
    required: field.required,
    isEmpty: !value || String(value).trim() === '',
    needsValidation: !value || String(value).trim() === '' || field.required
  };
}

// CALCUL DES STATISTIQUES
const totalFields = fieldsToValidate.length;
let filledFields = 0;
let requiredFields = 0;
let filledRequiredFields = 0;

for (const fieldKey in currentValues) {
  const field = currentValues[fieldKey];
  if (!field.isEmpty) filledFields++;
  if (field.required) {
    requiredFields++;
    if (!field.isEmpty) filledRequiredFields++;
  }
}

const completionRate = Math.round((filledFields / totalFields) * 100);
const requiredCompletionRate = Math.round((filledRequiredFields / requiredFields) * 100);

// Questions contextuelles par défaut
const contextualQuestions = [
  {
    id: 'commentaire_libre',
    question: 'Souhaitez-vous ajouter un commentaire libre avant la génération de la lettre ?',
    type: 'textarea',
    context: 'Commentaire final',
    category: 'cloture'
  }
];

// PREPARATION DE LA REPONSE COMPLETE POUR LE SITE WEB
const responseForWebsite = {
  success: true,
  fallback_mode: false,
  sessionId: sessionId,
  documentType: docType,

  extractedData: {
    employeur: extractedData.employeur || {},
    victime: extractedData.victime || {},
    accident: isMaladiePro ? null : (extractedData.accident || {}),
    maladie: isMaladiePro ? (extractedData.maladie || {}) : null,
    interim: isInterim ? (extractedData.interim || {}) : null,
    temoin: extractedData.temoin || {},
    tiers: extractedData.tiers || {}
  },

  validationFields: currentValues,
  contextualQuestions: contextualQuestions,

  completionStats: {
    totalFields: totalFields,
    filledFields: filledFields,
    completionRate: completionRate,
    requiredFields: requiredFields,
    filledRequiredFields: filledRequiredFields,
    requiredCompletionRate: requiredCompletionRate
  },

  nextStep: 'user_validation',
  instructions: {
    message: 'Vérifiez et complétez les informations extraites automatiquement.',
    validationEndpoint: '/webhook/validate-data',
    expectedFields: fieldsToValidate.map(f => f.section + '.' + f.field)
  },

  metadata: {
    extractionSource: 'mistral_ocr',
    aiModel: finalResult.metadata.ai_model || 'gemini-2.0-flash',
    extractionTimestamp: finalResult.timestamp,
    validationPreparedAt: new Date().toISOString()
  }
};

return {
  responseForWebsite: responseForWebsite,
  requestId: requestId
};

