// Init tracking - CONSERVE LES DONNEES BINAIRES
const items = $input.all();

for (const item of items) {
  item.json.requestId = 'req_' + Date.now() + '_' + Math.random().toString(36).substr(2, 9);
  item.json.startTime = Date.now();
}

return items;

// ===== VALIDATION DU DOCUMENT DAT V3 - CORRIGÉE =====
// Corrige les faux positifs sur les DAT intérimaires

const extractedText = $json.extracted_text || '';
const ocrSuccess = $json.ocr_success || false;

// Si OCR a échoué, on laisse passer pour que l'erreur soit gérée plus loin
if (!ocrSuccess || !extractedText || extractedText.trim().length < 50) {
  return {
    extracted_text: extractedText,
    ocr_success: ocrSuccess,
    validation: {
      is_valid_dat: false,
      has_multiple_dat: false,
      error: 'OCR_FAILED'
    },
    document_invalide: true,
    multiple_dat: false,
    valid: false
  };
}

// A) VÉRIFIER QUE C'EST UNE DAT (au moins 3 mots-clés sur 5)
const DAT_KEYWORDS = [
  { pattern: /accident\s*(du|de)\s*travail/i, name: 'accident du travail' },
  { pattern: /d[eé]claration/i, name: 'déclaration' },
  { pattern: /employeur/i, name: 'employeur' },
  { pattern: /victime|salari[eé]/i, name: 'victime/salarié' },
  { pattern: /cerfa|cpam|s[eé]curit[eé]\s*sociale|caisse.*maladie/i, name: 'CERFA/CPAM/Sécu' }
];

const foundKeywords = DAT_KEYWORDS.filter(kw => kw.pattern.test(extractedText));
const keywordNames = foundKeywords.map(kw => kw.name);
const isValidDAT = foundKeywords.length >= 3;

// B) DÉTECTER PLUSIEURS DAT - LOGIQUE CORRIGÉE
// On se base UNIQUEMENT sur les numéros de sécurité sociale de victimes DISTINCTES

// Pattern numéro de sécu: 1 ou 2 + 13 chiffres (avec ou sans espaces)
const secuPattern = /[12]\s*\d{2}\s*\d{2}\s*\d{2}\s*\d{3}\s*\d{3}\s*\d{2}/g;
const secuMatches = extractedText.match(secuPattern) || [];
const uniqueSecus = [...new Set(secuMatches.map(s => s.replace(/\s/g, '')))].filter(s => s.length === 15);

// Compter les blocs "L'EMPLOYEUR" ou "EMPLOYEUR" comme indicateur principal
// Une DAT unique a UN SEUL bloc employeur principal
const employeurHeaderPattern = /L['']?EMPLOYEUR|établissement\s+d['']?attache/gi;
const employeurHeaders = extractedText.match(employeurHeaderPattern) || [];

// Compter les numéros de formulaire CERFA spécifiques (N° 14463)
// IMPORTANT: Ne pas compter le mot "cerfa" seul (logo), uniquement "N° 14463" ou "N 14463"
const cerfaNumberPattern = /N[°o\s]*14463/gi;
const cerfaMatches = extractedText.match(cerfaNumberPattern) || [];

// Une DAT intérimaire a légitimement 2 SIRETs (employeur ETT + lieu accident)
// Donc on NE SE BASE PAS sur le nombre de SIRETs

// Critères de détection de PLUSIEURS DAT (plus stricts):
// 1. Plus d'un numéro de sécu VICTIME distinct
// 2. OU plus d'un bloc "L'EMPLOYEUR" (en-têtes de section)
// 3. OU plus d'un numéro de formulaire CERFA 14463
const hasMultipleDAT = (
  uniqueSecus.length > 1 ||           // Plusieurs victimes = plusieurs déclarations
  employeurHeaders.length > 2 ||       // Plus de 2 en-têtes employeur (tolérance pour OCR)
  cerfaMatches.length > 1              // Plus d'un numéro de formulaire
);

const detectedCount = Math.max(
  uniqueSecus.length, 
  Math.floor(employeurHeaders.length / 2),
  cerfaMatches.length
);

// C) RÉSULTAT
return {
  extracted_text: extractedText,
  ocr_success: ocrSuccess,
  validation: {
    is_valid_dat: isValidDAT,
    has_multiple_dat: hasMultipleDAT,
    detected_keywords: keywordNames,
    keyword_count: foundKeywords.length,
    unique_secu_numbers: uniqueSecus.length,
    employeur_headers_count: employeurHeaders.length,
    cerfa_numbers_count: cerfaMatches.length,
    detected_count: detectedCount,
    secus_found: uniqueSecus,
    debug: {
      employeur_headers: employeurHeaders,
      cerfa_matches: cerfaMatches
    }
  },
  // Flags de routing
  document_invalide: !isValidDAT,
  multiple_dat: isValidDAT && hasMultipleDAT,
  valid: isValidDAT && !hasMultipleDAT
};

// Erreur: Document non valide (pas une DAT)
const validation = $json.validation || {};
const requestId = $('Init Tracking').first().json.requestId;
const sessionId = 'invalid_' + Date.now() + '_' + Math.random().toString(36).substr(2, 6);

const invalidDocResponse = {
  success: false,
  error_type: 'INVALID_DOCUMENT',
  fallback_mode: false,
  sessionId: sessionId,

  errorDetails: {
    code: 'NOT_A_DAT',
    message: 'Le document uploadé ne semble pas être une déclaration d\'accident du travail.',
    suggestion: 'Veuillez uploader une déclaration d\'accident du travail (CERFA 14463) ou une déclaration de maladie professionnelle.',
    detected_keywords: validation.detected_keywords || [],
    keyword_count: validation.keyword_count || 0,
    minimum_required: 3
  },

  nextStep: 'upload_correct_document',
  instructions: {
    message: 'Document non reconnu comme une DAT. Veuillez uploader le bon document.',
    uploadEndpoint: '/webhook/upload',
    acceptedDocumentTypes: [
      'Déclaration d\'accident du travail (CERFA 14463)',
      'Déclaration de maladie professionnelle (CERFA 60-3950)'
    ]
  },

  metadata: {
    extractionSource: 'mistral_ocr',
    validationStage: 'pre_ai',
    extractionTimestamp: new Date().toISOString(),
    elapsed_time: Date.now() - $('Init Tracking').first().json.startTime
  }
};

return {
  responseForWebsite: invalidDocResponse,
  requestId: requestId
};

// Erreur: Plusieurs DAT détectées dans le document
const validation = $json.validation || {};
const requestId = $('Init Tracking').first().json.requestId;
const sessionId = 'multiple_' + Date.now() + '_' + Math.random().toString(36).substr(2, 6);

const multipleDocResponse = {
  success: false,
  error_type: 'MULTIPLE_DAT_DETECTED',
  fallback_mode: false,
  sessionId: sessionId,

  errorDetails: {
    code: 'MULTIPLE_DECLARATIONS',
    message: 'Plusieurs déclarations détectées dans votre document. Merci de n\'uploader qu\'UNE SEULE déclaration.',
    detected_count: validation.detected_count || 2,
    suggestion: 'Séparez vos documents et uploadez-les un par un.',
    details: {
      unique_secu_numbers: validation.unique_secu_numbers || 0,
      unique_accident_dates: validation.unique_accident_dates || 0,
      secus_found: validation.secus_found || [],
      dates_found: validation.dates_found || []
    }
  },

  nextStep: 'upload_single_document',
  instructions: {
    message: 'Veuillez séparer vos déclarations et uploader un seul document à la fois.',
    uploadEndpoint: '/webhook/upload',
    tip: 'Si votre PDF contient plusieurs pages pour la même déclaration, c\'est normal. Le problème survient uniquement si plusieurs déclarations différentes sont présentes.'
  },

  metadata: {
    extractionSource: 'mistral_ocr',
    validationStage: 'pre_ai',
    extractionTimestamp: new Date().toISOString(),
    elapsed_time: Date.now() - $('Init Tracking').first().json.startTime
  }
};

return {
  responseForWebsite: multipleDocResponse,
  requestId: requestId
};
